Map::Map()
{
}

void Map::setup()
{
    backGroundTileGrid.resize(GRID_ROWS, std::vector<Sprite>(GRID_COLS, Sprite()));
    tileGrid.resize(GRID_ROWS, std::vector<Sprite>(GRID_COLS, Sprite()));

    for (int i = 0; i < GRID_ROWS; ++i)
    {
        for (int j = 0; j < GRID_COLS; ++j)
        {
            backGroundTileGrid[i][j] = Sprite(TextureType::BACKGROUND, {j * 16.0f, i * 16.0f}, 16, 16);
        }
    }

    for (int i = 0; i < GRID_ROWS; ++i)
    {
        for (int j = 0; j < GRID_COLS; ++j)
        {
            if (TILES_DATA[i][j] == 1)
            {
                tileGrid[i][j] = Sprite(TextureType::WATER, {j * 16.0f, i * 16.0f}, 16, 16);
            }
            if (TILES_DATA[i][j] == 2)
            {
                tileGrid[i][j] = Sprite(TextureType::ROCK, {j * 16.0f, i * 16.0f}, 16, 16);
            }
        }
    }
}

void Map::update()
{
    std::vector<Particle> filteredParticles;

    auto lifeTimeExceeded = [](Particle particle)
    { return SDL_GetTicks() - particle.startLife < static_cast<Uint32>(particle.lifeTime); };

    std::copy_if(particles.begin(), particles.end(), std::back_inserter(filteredParticles), lifeTimeExceeded);

    particles = filteredParticles;

    for (size_t i = 0; i < particles.size(); i++)
    {
        particles[i].update();
    }
}

void Map::render()
{
    for (int i = 0; i < GRID_ROWS; ++i)
    {
        for (int j = 0; j < GRID_COLS; ++j)
        {
            backGroundTileGrid[i][j].render();
        }
    }

    for (int i = 0; i < GRID_ROWS; ++i)
    {
        for (int j = 0; j < GRID_COLS; ++j)
        {
            tileGrid[i][j].render();
        }
    }

    for (size_t i = 0; i < particles.size(); i++)
    {
        particles[i].render();
    }
}

void Map::createParticle(Particle _particle)
{
    particles.push_back(_particle);
}
